const idx=lunr.Index.load(JSON.parse(searchIDX));function searchSite(e){return convertSearchResultsToLinks(idx.search(e).map(e=>site.find(t=>e.ref===t.id)),e)}function convertSearchResultsToLinks(e,t){return e?e.map((function(e,t){return`<h5 class="search-title" style="margin-bottom:10px;text-align: left;"><a target="_top" href="${e.page}" class="search-link">${e.name}</a></h5>`})):[]}